const API_KEY = ''AIzaSyCWGlr4riq9c0M9p21s-qPfNCvGU3hglfc'';

const prompt = \\`You are an SEO content writer. Generate content for a solar calculator page split into two distinct sections.

STRICT SEO DATA:
- Keyword: ""Solar panels in Austin""
- City: Austin, TX
- Sun Hours: 5.5 peak hours \(High Potential\)
- Savings Range: $27,000 to $80,000 \(25-year estimate, depending on electricity usage\)
- Urgency: Rising utility rates, inflation protection. \(NO Tax Credit mentions\).

OUTPUT REQUIREMENTS \(JSON ONLY\):
1. title: SEO Title starting with ""Solar panels in Austin""
2. meta_description: Click-worthy description. Use the savings RANGE.
3. h1: Exact keyword ""Solar panels in Austin""
4. intro_content: ONE punchy HTML paragraph \(<p> tag only\). Keep it 40-50 words.
5. detailed_content: TWO HTML paragraphs \(with <h3> headers\). Focus on ""Escaping Rate Hikes"" and ""Long-term Wealth"".

Return ONLY valid JSON with no markdown formatting.\\`;

fetch\(''https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key='' + API_KEY, {
  method: ''POST'',
  headers: { ''Content-Type'': ''application/json'' },
  body: JSON.stringify\({
    contents: [{ parts: [{ text: prompt }] }],
    generationConfig: {
      temperature: 0.7,
      maxOutputTokens: 1024,
      responseMimeType: ''application/json'',
    },
  }\),
}\)
.then\(r => r.json\(\)\)
.then\(data => {
  console.log\(''=== FULL RESPONSE ===''\);
  console.log\(JSON.stringify\(data, null, 2\)\);

  const text = data.candidates?.[0]?.content?.parts?.[0]?.text;
  console.log\(''\\n=== TEXT CONTENT ===''\);
  console.log\(text\);

  if \(text\) {
    try {
      const parsed = JSON.parse\(text\);
      console.log\(''\\n=== PARSED KEYS ===''\);
      console.log\(Object.keys\(parsed\)\);
    } catch \(e\) {
      console.log\(''\\nFailed to parse:'', e.message\);
    }
  }
}\)
.catch\(e => console.error\(''Error:'', e\)\);
")

